gress;        item8.style.transform = `translate($&#123;x8&#125;%, $&#123;y8&#125;%) translate3d(0px, 0px, -3916.05px) rotateX($&#123;r8&#125;deg)`;        // item9 translate(33.6702%, -62.2242%) translate3d(0px, 0px, -2486.3px) rotateX(-28.5deg) -&gt; translate(312.197%, -576.957%) translate3d(0px, 0px, -2486.3px)  rotateX(0deg);        const x9 = 33.6702 - (33.6702 - 312.197) * progress;        const y9 = -62.2242 - (-62.2242 - -576.957) * progress;        const r9 = 28.5 - (28.5 - 0) * progress;        item9.style.transform = `translate($&#123;x9&#125;%, $&#123;y9&#125;%) translate3d(0px, 0px, -2486.3px) rotateX($&#123;r9&#125;deg)`;        // item10 translate(-43.3808%, -26.9066%) translate3d(0px, 0px, -3390.29px) rotateX(-44.3126deg) -&gt; translate(-402.236%, -249.484%) translate3d(0px, 0px, -3390.29px)  rotateX(0deg);        const x10 = -43.3808 - (-43.3808 - -402.236) * progress;        const y10 = -26.9066 - (-26.9066 - -249.484) * progress;        const r10 = 44.3126 - (44.3126 - 0) * progress;        item10.style.transform = `translate($&#123;x10&#125;%, $&#123;y10&#125;%) translate3d(0px, 0px, -3390.29px) rotateX($&#123;r10&#125;deg)`;        // item11 translate(43.667%, 23.8554%) translate3d(0px, 0px, -4233.88px) rotateX(-51.7605deg) -&gt; translate(404.89%, 221.192%) translate3d(0px, 0px, -4233.88px)  rotateX(0deg);        const x11 = 43.667 - (43.667 - 404.89) * progress;        const y11 = 23.8554 - (23.8554 - 221.192) * progress;        const r11 = 51.7605 - (51.7605 - 0) * progress;        item11.style.transform = `translate($&#123;x11&#125;%, $&#123;y11&#125;%) translate3d(0px, 0px, -4233.88px) rotateX($&#123;r11&#125;deg)`;        // item12 translate(-42.7257%, 12.9929%) translate3d(0px, 0px, -4766.62px) rotateX(-38.0214deg) -&gt; translate(-396.162%, 120.473%) translate3d(0px, 0px, -4766.62px)  rotateX(0deg);        const x12 = -42.7257 - (-42.7257 - -396.162) * progress;        const y12 = 12.9929 - (12.9929 - 120.473) * progress;        const r12 = 38.0214 - (38.0214 - 0) * progress;        item12.style.transform = `translate($&#123;x12&#125;%, $&#123;y12&#125;%) translate3d(0px, 0px, -4766.62px) rotateX($&#123;r12&#125;deg)`;        // item13 translate(-46.0651%, -12.2405%) translate3d(0px, 0px, -3042.13px) rotateX(-44.642deg) -&gt; translate(-427.125%, -113.497%) translate3d(0px, 0px, -3042.13px)  rotateX(0deg);        const x13 = -46.0651 - (-46.0651 - -427.125) * progress;        const y13 = -12.2405 - (-12.2405 - -113.497) * progress;        const r13 = 44.642 - (44.642 - 0) * progress;        item13.style.transform = `translate($&#123;x13&#125;%, $&#123;y13&#125;%) translate3d(0px, 0px, -3042.13px) rotateX($&#123;r13&#125;deg)`;      &#125;    &#125;,  &#125;)&#125;
