orm: translate(21.4086%, -43.4698%)            translate3d(0px, 0px, -3200.35px) rotateX(-51.8022deg);        &#125;        &amp;:nth-child(2) &#123;          transform: translate(55.7288%, 68.2112%)            translate3d(0px, 0px, -2559.09px) rotateX(-22.5208deg);        &#125;        &amp;:nth-child(3) &#123;          transform: translate(157.324%, 74.8789%)            translate3d(0px, 0px, -2775.72px) rotateX(-36.45deg);        &#125;        &amp;:nth-child(4) &#123;          transform: translate(-36.6797%, -83.794%)            translate3d(0px, 0px, -4815.3px) rotateX(-31.3392deg);        &#125;        &amp;:nth-child(5) &#123;          transform: translate(35.3928%, -48.4764%)            translate3d(0px, 0px, -3814.69px) rotateX(-36.6888deg);        &#125;        &amp;:nth-child(6) &#123;          transform: translate(59.4486%, -21.9%)            translate3d(0px, 0px, -3583.52px) rotateX(-31.1744deg);        &#125;        &amp;:nth-child(7) &#123;          transform: translate(-24.4454%, 46.6414%)            translate3d(0px, 0px, -2463.66px) rotateX(-42.1613deg);        &#125;        &amp;:nth-child(8) &#123;          transform: translate(-40.5806%, 53.3091%)            translate3d(0px, 0px, -3916.05px) rotateX(-45.8681deg);        &#125;        &amp;:nth-child(9) &#123;          transform: translate(33.6702%, -62.2242%)            translate3d(0px, 0px, -2486.3px) rotateX(-28.5deg);        &#125;        &amp;:nth-child(10) &#123;          transform: translate(-43.3808%, -26.9066%)            translate3d(0px, 0px, -3390.29px) rotateX(-44.3126deg);        &#125;        &amp;:nth-child(11) &#123;          transform: translate(43.667%, 23.8554%)            translate3d(0px, 0px, -4233.88px) rotateX(-51.7605deg);        &#125;        &amp;:nth-child(12) &#123;          transform: translate(-42.7257%, 12.9929%)            translate3d(0px, 0px, -4766.62px) rotateX(-38.0214deg);        &#125;        &amp;:nth-child(13) &#123;          transform: translate(-46.0651%, -12