//获取URL某个参数的方法function getUrlParam(paramName) &#123;    const url = window.location.href;    const regex = new RegExp(&#x27;[?&amp;]&#x27; + paramName + &#x27;=([^&amp;#]*)&#x27;);    const match = regex.exec(url);    return match ? decodeURIComponent(match[1]) : null;&#125;

