function animation(&#123; from, to, duration = 1000, onUpdate &#125;) &#123;  let value = from;  const start = Date.now();  const speed = (to - from) / duration;  //让value值变化一点  function _run() &#123;    //1.改变value值    const t = Date.now() - start;    if (t &gt;= duration) &#123;      value = to;      onUpdate &amp;&amp; onUpdate(value);      return;    &#125;    value = from + t * speed;    onUpdate &amp;&amp; onUpdate(value);    //2.注册下一次变化    requestAnimationFrame(_run);  &#125;  _run();&#125;

